import { baseToken } from "./constants";

class Api {
  constructor(url, headers, token) {
    this._url = url;
    this._authorizationToken = token;
    //this._headers = headers;
    this._headers =headers//{Authorization:this._authorizationToken, 'Content-Type': 'application/json'};
  }
  _getAuthHeader() {
    const jwt = localStorage.getItem('jwt');
    return jwt ? { Authorization: `Bearer ${jwt}` } : {};
  }
  getUserInfo() {
    return fetch(`${this._url}/users/me`, {
      method: 'GET',
      headers: {
        ...this._headers,
        //Authorization: `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MzA0YjYyYTA0NzFjMTdlNGYxMTczMzYiLCJpYXQiOjE2NjEyNjU4OTgsImV4cCI6MTY2MTg3MDY5OH0.-oclzIeC4662HrGxIwxFaT7VNgL80WlPbuPno8lFlkc`
        authorization: this._authorizationToken 
      }})
    .then(this.checkResult);
  }
  getInitialCards() {
    return fetch(this._url +'/cards', {
      method: 'GET',
      headers: {
        ...this._headers,
        authorization: `Bearer ${localStorage.getItem('jwt')}` 
        //Authorization: `Bearer ${localStorage.getItem('jwt')}` 
      }})
    .then(this.checkResult);
  }
  editProfile(data){
    return fetch(this._url +'/users/me', {
        method: 'PATCH',
        headers: this._headers,
        body: JSON.stringify(data)
      })
    .then(this.checkResult);
  }
  addPlace(data){
    return fetch(this._url +'/cards', {
      method: 'POST',
      headers: this._headers,
      body: JSON.stringify(data)
    })
    .then(this.checkResult);
  }
  deletePost(cardId) {
    return fetch(this._url + '/cards/' + cardId, {
      method: 'DELETE',
      headers: this._headers,
    })
    .then(this.checkResult);
  }
  addLike(cardId){
    return fetch(this._url + '/cards/likes/' + cardId, {
      method: 'PUT',
      headers: this._headers,
    })
    .then(this.checkResult);
  }
  deleteLike(cardId){
    return fetch(this._url + '/cards/likes/' + cardId, {
      method: 'DELETE',
      headers: this._headers,
    })
    .then(this.checkResult);
  }
  addAvatar(avatar){
    return fetch(this._url +'/users/me/avatar', {
      method: 'PATCH',
      headers: this._headers,
      body: JSON.stringify(avatar)
    })
    .then(this.checkResult);
  }
  checkResult = res => {
    if (res.ok) {
      return res.json();
    } else {
      return Promise.reject(`Ошибка: ${res.status}`);
    }
  }
}
