import React, { useEffect, useState } from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from './ImagePopup';
////////////////
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPopup from "./AddPopup";
import DeletePopup from "./DeletePopup";
import { Route, Routes, useNavigate, useLocation } from 'react-router-dom';
import {api}  from '../utils/Api'
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
///////1/////////
import Register from '../components/Register';
import Login from '../components/Login';
import RequireAuth from './ProtectedRoute';
import * as auth from '../components/auth';

function App() {
  ///////////1//////////////////////////////////
  const [loggedIn, setLoggedIn] = useState(false);
  const [userEmail, setUserEmail] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  const [isAddCardPopupOpen, setisAddCardPopupOpen] = React.useState(false);
  function handleAddCardClick() {
    setisAddCardPopupOpen(true);
  }
  const [isAvatarProfilePopupOpen, setisAvatarProfilePopupOpen] = React.useState(false);
  function handleAvatarProfileClick() {
    setisAvatarProfilePopupOpen(true);
  }
  const [selectedCard, setSelectedCard] = React.useState(null);
  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImagePopupOpenen(true);
  }
  const [isImagePopupOpenen, setIsImagePopupOpenen] = React.useState(false);
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setisAddCardPopupOpen(false);
    setisAvatarProfilePopupOpen(false);
    setSelectedCard(null);
    setIsDeletePopupOpenen(false);
    setIsImagePopupOpenen(false);
  }
  ////////////////////////////////////////////
  const [currentUser, setCurrentUser] = React.useState({});
  React.useEffect(() => {
    if (loggedIn) {
      api
        .getUserInfo()
        .then((data) => {
          setCurrentUser(data);
        })
        .catch((err) => console.log(err));
    }
  }, [loggedIn]);

  const [cards, setCards] = React.useState([]);
  React.useEffect(() => {
    if (loggedIn) {
      api
        .getInitialCards()
        .then((data) => {
          setCards(data);
        })
        .catch((err) => console.log(err));
    } 
  }, [loggedIn]);
  /* React.useEffect(() => {

      api
        .getUserInfo()
        .then((data) => {
          setCurrentUser(data);
        })
        .catch((err) => console.log(err));

  }, []);

  const [cards, setCards] = React.useState([]);
  React.useEffect(() => {

      api
        .getInitialCards()
        .then((data) => {
          setCards(data);
        })
        .catch((err) => console.log(err));

  }, []); */
  ///////////////////////////////////////////
  function handleCardLike(card){
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    if (isLiked) {
      api
        .deleteLike(card._id)
        .then((newCard) => {
          setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
        })
        .catch((err) => {
          console.log(err);

          return [];
        });
    } else {
      api
        .addLike(card._id)
        .then((newCard) => {
          setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
        })
        .catch((err) => {
          console.log(err);

          return [];
        });
    }

  } 
////////////////
  function handleEditUser(data) {

    api
      .editProfile(data)
      .then((data) => {
        setCurrentUser(data);
        //console.log(data);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleUpdateAvatar(data) {
    api
      .addAvatar(data)
      .then((data) => {
        setCurrentUser(data);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }
  function handleAddPlace(data) {
    api
      .addPlace(data)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }
  const [isDeletePopupOpenen, setIsDeletePopupOpenen] =React.useState(false);
  function handleCardDeleteClick(card) {
    setIsDeletePopupOpenen(true);
    ////

    ////
    setSelectedCard(card);
  }
  function handleDeletePlace(card) {
    api
      .deletePost(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id));
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }
/////////////1/////////////
  useEffect(() => {
    handleTokenCheck(location.pathname);
  // запрос в API за пользовательскими данными
    Promise.all([ 
      api.getUserInfo(),
      api.getInitialCards()
    ])
    .then((res) => {
      setCurrentUser(res[0]);
      setCards(res[1])
    })
    .catch((err) => {
      console.log(err);
      return [];
    })
  }, []);

  const handleTokenCheck = (path) => {
    if (localStorage.getItem("jwt")) {
      const jwt = localStorage.getItem("jwt");
    // проверяем токен пользователя
      auth.checkToken(jwt).then((res) => {
        if (res) {
          setLoggedIn(true);
          setUserEmail(res.data.email);
          navigate(path);
        }
      })
      .catch((err) => console.log(err));
    }
  };

  const handleLogin = (email,password) => {

    /* setLoggedIn(true);
    setUserEmail(email); */
    auth
      .authorize(email,password)
      .then((res) => {
        if (res.token) {
          setValues({
            email: "",
            password: "",
          })
          localStorage.setItem('jwt', res.token);
          onLogin(values.email);  // обновляем стейт внутри App.js
          navigate("/"); // и переадресуем пользователя! 
        }
        ////////////
        else {
          setInfoPopup({
            message: 'Что-то пошло не так!',
            errorLogin: false,
            isOpenLoginPopup: false
          });
          openPopup();
        }
        /////////////
      })
      .catch((err) => console.log(err));
  };

  const handleLogout= (evt) => {
    evt.preventDefault();
    localStorage.removeItem('jwt');
    setLoggedIn(false);
    setUserEmail('');
    navigate('/sign-in')

  }
//////////////////////////
  //const navigate = useNavigate();
  const [isInfoTooltipSucceed, setIsInfoTooltipSucceed] = React.useState({
    isOpen: false,
    isSucceed: false,
  });

  const handleRegister = (email, password) => {
    auth.register(password, email)
      .then((res) => {
        if (res) {
          navigate("/sign-in");

          setIsInfoTooltipSucceed({
            isOpen: true,
            isSucceed: true,
          });
        }
      })
      .catch((err) => {
        console.log(`Ошибка регистрации ${err}`);
        setIsInfoTooltipSucceed({
          isOpen: true,
          isSucceed: false,
        });
      });
      /* auth
        .register(
          values.email,
          values.password
        )
        .then((res) => {
          if(res.data){
            setInfoPopup({
              message: 'Успешно зарегестрировались',
              errorLogin: true,
              isOpenLoginPopup: false
            });
            openPopup();
          } else {
            setInfoPopup({
              message: 'Что-то пошло не так!',
              errorLogin: false,
              isOpenLoginPopup: false
            });
            openPopup();
          }
        });*/
  }; 
  const [infoPopup, setInfoPopup] = useState({
    isOpenLoginPopup: false,
    errorLogin: false,
    message: ''
  });
  /* const openPopup = () => {
    setInfoPopup((prevState) => ({
      ...prevState,
      isOpenLoginPopup: true,
    }));
  }

  const closePopup = () => {
    setInfoPopup((prevState) => ({
      ...prevState,
      isOpenLoginPopup: false,
    }));
    if (infoPopup.errorLogin) navigate("/sign-in");
  }; */
